WITH CASE_LIST AS (
    SELECT C.CASE_NUMBER
         , P.PROJECT_NAME
         , P.SERVICE_STATE
         , TO_DATE(C.CREATED_DATE)           AS CASE_CREATED_DATE
         , TO_DATE(NVL(P.INSTALLATION_COMPLETE,
                       CT.TRANSACTION_DATE)) AS INSTALL_DATE
         , IFF(DATEDIFF(dd,
                        INSTALL_DATE,
                        CASE_CREATED_DATE) >= 120,
               'GTE 120',
               'LT 120')                     AS AGE_BUCKET_DAYS
         , DATEDIFF(dd,
                    INSTALL_DATE,
                    CASE_CREATED_DATE) AS TO_DEFAULT_AGE
         , DATEDIFF(dd,
                    INSTALL_DATE,
                    CURRENT_DATE)            AS CASE_AGE_DAYS
